import React from "react";
import styled from "styled-components";

const ButtonWrapper = styled.button`
  border: none;
  outline: none;
  color: #fff;
  padding: 8px 1.5em;
  font-size: ${({ size }) => (size ? size + "px" : "18px")};
  font-weight: 600;
  border-radius: 3px;
  background-color: #dc67ff;
  cursor: pointer;
  transition: all 200ms ease-in-out;
  cursor: url(data:image/svg+xml;base64,)
      4 4,
    auto;
  &:hover {
    background-color: #9d3fb7;
    transform: scale(1.1);
  }

  &:focus {
    outline: none;
  }
`;

export function Button(props) {
  const { size } = props;

  return (
    <ButtonWrapper size={size} className={props.className}>
      {props.children}
    </ButtonWrapper>
  );
}
